<template>
	<div>
		<div id="main" class="chart"></div>
	</div>
</template>

<script>
	import {
		init,
		dispose,
		extension
	} from "klinecharts";
	export default {
		props: {
			currentId: {
				default: "",
			},
			currentItem: {
				default: "",
			},
		},
		data() {
			return {
				chart: null,
				show: true,
				timer: null,
				htmlId: "",
			};
		},
		watch: {
			currentId: {
				handler(newValue, oldValue) {
					if (newValue && newValue !== oldValue) {
						if (this.chart !== null) {
							dispose(this.chart.id);
						}
						if (this.timer !== null) {
							this.close();
						}
						this.getLine(newValue);
						this.open();
					}
				},
				// deep: true,  // 深度监听
				immediate: true, // 第一次改变就执行
			},
		},
		beforeDestroy() {
			this.close();
		},
		methods: {
			open() {
				this.timer = setInterval(() => {
					this.getLine(this.currentId);
				}, 60 * 1000);
			},
			close() {
				clearInterval(this.timer);
			},
			getLine(id) {
				this.$server.post("/trade/kline", {
					//id,
					symbol: this.currentId,
					type: this.$stockType,
					kline_type: '1'
				}).then((res) => {
					if (res.status == 1) {
						if (res.data.hasOwnProperty("detail")) {
							this.setEchartOption([]);
							return;
						}

						let dataList = [];
						let arr = res.data.data;
						for (let i = 0; i < arr.length; i++) {
							let item = arr[i];
							// 数据当中有很部分点信息会返回空值
							if (item[1]) {
								let kLineModel = {
									open: 0,
									close: item[1],
									high: 0,
									low: 0,
									timestamp: item[0],
								};

								dataList.push(kLineModel);
							}
						}

						let Arr = dataList.filter((item) => item); //过滤空值
						this.setEchartOption(Arr);
					}
				});
			},
			setEchartOption(dataList) {
				this.chart = init(document.getElementById("main"));

				// 没有数据时显示图层
				if (dataList.length == 0) {
					this.htmlId = this.chart.createHtml({
							id: "html_1",
							position: "content",
							style: {
								zIndex: 12,
								textAlign: "center",
								top: "50%",
								left: "50%",
								transform: "translate(-50%,-50%)",
							},
