								offsetRight: 8,
								borderRadius: 4,
								borderSize: 1,
								borderColor: "#3f4254",
								backgroundColor: "rgba(17, 17, 17, .3)",
							},
							text: {
								size: 12,
								family: "Helvetica Neue",
								weight: "normal",
								color: "#94989B",
								marginLeft: 8,
								marginTop: 6,
								marginRight: 8,
								marginBottom: 0,
							},
						},
					},
					grid: {
						show: false,
						// 网格水平线
						horizontal: {
							show: false,
							size: 1,
							color: "#fff",
							// 'solid'|'dash'
							style: "dash",
							dashValue: [2, 2],
						},
						// 网格垂直线
						vertical: {
							show: false,
							size: 1,
							color: "#fff",
							// 'solid'|'dash'
							style: "dash",
							dashValue: [2, 2],
						},
					},
					xAxis: {
						tickLine: {
							show: false,
							color: "#fff",
						},
						// x轴分割文字
						tickText: {
							show: true,
							color: "#94989B",
							family: "PingFang SC",
							weight: "500",
							size: 12,
							paddingTop: 12,
							paddingBottom: 6,
						},
						axisLine: {
							show: true,
							color: "#FAFAFA",
						},
					},
					yAxis: {
						tickLine: {
							show: false,
							size: 1,
							length: 3,
							color: "#fff",
						},
						tickText: {
							show: true,
							color: "#94989B",
							family: "PingFang SC",
							weight: "500",
							size: 12,
							paddingLeft: 12,
							paddingRight: 6,
						},
						axisLine: {
							show: false,
							color: "#FAFAFA",
						},
					},
				});
				this.chart.applyNewData(dataList);
			},
		},
	};
</script>

